package vo;

public class ChargingVo {

	int charging_int;
	int charging_payment_idx;
	int charging_member_idx;
	String charging_regdate;
	String charging_use;

	public ChargingVo() {
		// TODO Auto-generated constructor stub
	}

	public int getCharging_int() {
		return charging_int;
	}

	public void setCharging_int(int charging_int) {
		this.charging_int = charging_int;
	}

	public int getCharging_payment_idx() {
		return charging_payment_idx;
	}

	public void setCharging_payment_idx(int charging_payment_idx) {
		this.charging_payment_idx = charging_payment_idx;
	}

	public int getCharging_member_idx() {
		return charging_member_idx;
	}

	public void setCharging_member_idx(int charging_member_idx) {
		this.charging_member_idx = charging_member_idx;
	}

	public String getCharging_regdate() {
		return charging_regdate;
	}

	public void setCharging_regdate(String charging_regdate) {
		this.charging_regdate = charging_regdate;
	}

	public String getCharging_use() {
		return charging_use;
	}

	public void setCharging_use(String charging_use) {
		this.charging_use = charging_use;
	}

}
